mapper.select:
SELECT CODE_GRP, CODE, NAME, CODE2, NAME2, CODE3, NAME3, CODE4, NAME4, CODE5, NAME5, SEQ, UZ_YN FROM HMFB_006 WHERE CODE_GRP = #{codeGrp} AND UZ_YN = 'Y'
AND CODE = #{code}
ORDER BY SEQ

mapper.update:

    	UPDATE HMFB_006 SET
			NAME = #{name},
			SEQ = #{seq},
			UPD_DT = #{updDt},
			UPD_TM = #{updTm} 
    	WHERE CODE_GRP = #{codeGrp}
    	  AND CODE = #{code}

mapper.delete:

DELETE FROM HMFB_006
WHERE CODE_GRP = #{codeGrp}
  AND CODE = #{code}
