mapper.insert:

INSERT INTO BATCH_JOB
(
    JOB_CODE,
	JOB_DESC,
	JOB_CYCLE,
	JOB_TYPE,
	PROGRAM_NAME,
	COMMIT_COUNT,
	INPUT_DATA_SELECTOR,
	OUTPUT_DATA_SELECTOR,
	EXCEPTION_POLICY,
	CRON_EXPRESSION,
	USE_YN,
	RUN_PARAM,
	REGISTER_DTM,
	REGISTER_USER_ID,
	MODIFY_DTM,
	MODIFY_USER_ID
) VALUES(
     #{jobCode}
	,#{jobDesc}
	,#{jobCycle}
	,#{jobType}
	,#{programName}
	,#{commitCount}
	,#{inputDataSelector}
	,#{outputDataSelector}
	,#{exceptionPolicy}
	,#{cronExpression}
	,#{useYn}
	,#{runParam}
	,#{registerDtm}
	,#{registerUserId}
	,#{modifyDtm}
	,#{modifyUserId}
)

mapper.update:

UPDATE BATCH_JOB          
   SET JOB_DESC              = #{jobDesc}
      ,JOB_CYCLE             = #{jobCycle}
      ,JOB_TYPE              = #{jobType}
      ,PROGRAM_NAME          = #{programName}
      ,COMMIT_COUNT          = #{commitCount}
      ,INPUT_DATA_SELECTOR   = #{inputDataSelector}
      ,OUTPUT_DATA_SELECTOR  = #{outputDataSelector}
      ,EXCEPTION_POLICY      = #{exceptionPolicy}
      ,CRON_EXPRESSION       = #{cronExpression}
      ,USE_YN                = #{useYn}
      ,RUN_PARAM             = #{runParam}
      ,REGISTER_DTM          = #{registerDtm}
      ,REGISTER_USER_ID      = #{registerUserId}
      ,MODIFY_DTM            = #{modifyDtm}
      ,MODIFY_USER_ID        = #{modifyUserId}
WHERE JOB_CODE = #{jobCode}
