mapper.update:

    	UPDATE HMFB_003 SET
    		LST_LGN_DTTM = #{lstLgnDttm},
    		UPD_DT = #{updDt},
			UPD_TM = #{updTm} 
    	WHERE USER_ID = #{userId}

mapper.select:
SELECT H3.SEQ_NO, H3.USER_ID, H3.PSWD, H3.FLNM, H3.PHN_NO, H3.DEPART, H3.AUTH_DVCD, H6.NAME FROM HMFB_003 H3 LEFT JOIN HMFB_006 H6 ON H6.CODE_GRP = 'AUTH_DVCD' AND H6.UZ_YN = 'Y' AND H6.CODE = H3.AUTH_DVCD WHERE DEL_YN = 'N'
AND H3.FLNM LIKE CONCAT('%',#{flnm},'%')
AND H3.AUTH_DVCD = #{authDvcd}
ORDER BY H3.REG_DT LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

SELECT COUNT(SEQ_NO) FROM HMFB_003 WHERE DEL_YN = 'N'
AND FLNM LIKE CONCAT('%',#{flnm},'%')
AND AUTH_DVCD = #{authDvcd}

